#! /usr/bin/env dcli

import 'dart:io';
import 'package:dcli/dcli.dart';
import 'package:pub_release/src/git_hub_release.dart';

/// dcli script generated by:
/// dcli create delete.dart
///
/// See
/// https://pub.dev/packages/dcli#-installing-tab-
///
/// For details on installing dcli.
///

void main(List<String> args) {
  final gh = SimpleGitHub(
      username: 'bsutton',
      apiToken: '0a567b622f26b97cfa9a88a6b61da1e328a8146b',
      owner: 'bsutton',
      repository: 'pub_release');

  gh.auth();

  final tagName = 'latest-${Platform.operatingSystem}';

  /// If there is an existing tag we overwrite it.
  final old = waitForEx(gh.getByTagName(tagName: tagName));
  if (old != null) {
    // ignore: avoid_print
    print('replacing release $tagName');
  }
  gh.listReferences();
  gh.deleteTag('latest-linux');
  gh.listReferences();

  gh.dispose();
}
